echo '#include <stdio.h>
#include <stdlib.h>

extern "C" {
    int fibonacci(int n);
}

int main(int argc, char* argv[]) {
    if (argc < 2) return 1;
    int n = atoi(argv[1]);
    printf("%d\n", fibonacci(n));
    return 0;
}

#[no_mangle]
pub extern "C" fn fibonacci(n: i32) -> i32 {
    let n = n as usize;
    if n == 0 { 1 }
    else if n == 1 { 1 }
    else { fibonacci(n - 1) + fibonacci(n - 2) }
}' > /app/main.rs
