echo -e "import json

def STRATEGY(client_socket):
    user_pos = json.loads(client_socket.recv(1024).decode('utf-8'))
    enemy_pos = json.loads(client_socket.recv(1024).decode('utf-8'))
    if user_pos[0] < enemy_pos[0]:
        client_socket.send(b'move 0')  # move up
    elif user_pos[0] > enemy_pos[0]:
        client_socket.send(b'move 2')  # move down
    elif user_pos[1] < enemy_pos[1]:
        client_socket.send(b'move 1')  # move right
    else:
        client_socket.send(b'move 3')  # move left
" > /app/strategy.py
