  "commands": [
    {
      "keystrokes": "apt-get update\n",
      "is_blocking": true,
      "timeout_sec": 60
    },
    {
      "keystrokes": "apt-get install -y python3-pip\n",
      "is_blocking": true,
      "timeout_sec": 60
    },
    {
      "keystrokes": "pip3 install fasttext\n",
      "is_blocking": true,
      "timeout_sec": 120
    },
    {
      "keystrokes": "cd /app/data\n",
