    {
      "keystrokes": "cat yelp_data.txt | fasttext supervised -c -i - -o model.bin -r 10.0 -v 1 -m 150M\n",
      "is_blocking": true,
      "timeout_sec": 300
    }
  ],
  "is_task_complete": false
}

