echo -e '#include <stdio.h>
#include <stdlib.h>
#include <mpi.h>
#define SIZE 100
typedef struct { double x, y; } Particle;
void simulate(Particle *particles, int n) {
    for (int i = 0; i < n; i++) {
        particles[i].x = rand() % SIZE;
        particles[i].y = rand() % SIZE;
    }
    int rank;
    MPI_Comm_rank(MPI_COMM_WORLD, &rank);
    FILE *output = fopen("mpi_output.txt", "a");
    if (output) {
        fprintf(output, "Rank %d: n size
%d %d
", rank, n, SIZE);
        for (int i = 0; i < n; i++) {
            fprintf(output, "%f %f
", particles[i].x, particles[i].y);
        }
        fclose(output);
    }
} 
int main(int argc, char *argv[]) {
    MPI_Init(&argc, &argv);
    int n = 100;
    Particle *particles = malloc(n * sizeof(Particle));
    simulate(particles, n);
    free(particles);
    MPI_Finalize();
    return 0;
}' > mpi.c
