commands.keystrokes:
echo 'server {
  listen 8080;
  root /var/www/html;
  index index.html;
  access_log /var/log/nginx/benchmark-access.log '$time_local' '$request_method' '$status' '"$http_user_agent";
  limit_req_zone $binary_remote_addr zone=one:10m rate=10s;
  location / {
    limit_req zone=one;
  }
  error_page 404 /404.html;
  location = /404.html {
    internal;
  }
}
' > /etc/nginx/conf.d/benchmark-site.conf
