echo '#include <stdio.h>
#include <stdlib.h>

// Function to generate an image file in PPM format
void generate_image() {
    int width = 100; // Set the width of the image
    int height = 100; // Set the height of the image
    FILE *f = fopen("reconstructed.ppm", "wb");
    fprintf(f, "P6
%d %d
255
", width, height);
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            fputc((unsigned char)(x * 255 / width), f); // Red channel
            fputc((unsigned char)(y * 255 / height), f); // Green channel
            fputc(0, f); // Blue channel
        }
    }
    fclose(f);
}

int main() {
    generate_image();
    return 0;
}' > image.c
