echo "const fs = require('fs');
const process = require('process');

if (process.argv.length < 3) {
    console.error('Usage: node extract.js <file>');
    process.exit(1);
}

const file = process.argv[2];

fs.readFile(file, (err, data) => {
    if (err) {
        console.error(err);
        return;
    }
    const memoryValues = {};
    for (let i = 0; i < data.length; i += 4) {
        const address = (i + 4194304).toString();
        const value = data.readUInt32LE(i);
        memoryValues[address] = value;
    }
    console.log(JSON.stringify(memoryValues));
});" > extract.js
